    class Solution_30_SubstringWithConcatenationOfAllWords
    {
        List<string> strList = new List<string>();

        public IList<int> FindSubstring_First(string s, string[] words)
        {
            List<int> resultList = new List<int>();
            Dictionary<string, int> dic = new Dictionary<string, int>();

            if (words == null || words.Length == 0)
            {
                return resultList;
            }
            else
            {
                int len = words[0].Length;
                int totalLength = len * words.Length;
                foreach (string word in words)
                {
                    if (!dic.ContainsKey(word))
                    {
                        dic.Add(word, 1);
                    }
                    else
                    {
                        dic[word]++;
                    }
                }

                strList.Clear();
                StringBuilder str = new StringBuilder();
                BuildLongString(str, dic);


                for (int i = 0; i <= s.Length - totalLength; i++)
                {
                    string sub = s.Substring(i, totalLength);

                    if (strList.Contains(sub))
                    {
                        resultList.Add(i);
                    }
                }

                return resultList;

            }
        }

        public void BuildLongString(StringBuilder str, Dictionary<string, int> dicOrigin)
        {
            if (dicOrigin.Count == 0)
            {
                strList.Add(str.ToString());
            }
            else
            {
                //Dictionary<string, int> dic = dicOrigin.ToDictionary(k => k.Key, k => k.Value);
                Dictionary<string, int> dic = new Dictionary<string, int>(dicOrigin);

                foreach (string key in dicOrigin.Keys)
                {
                    dic[key]--;
                    str.Append(key);

                    if (dic[key] == 0)
                    {
                        dic.Remove(key);
                    }

                    BuildLongString(str, dic);
                }
            }
        }



