        public IList<int> FindSubstring_Second(string s, string[] words)
        {
            Dictionary<string, int> dic = new Dictionary<string, int>();

            List<int> resultList = new List<int>();


            if (words != null || words.Length != 0)
            {
                int len = words[0].Length;

                if (s.Length < len * words.Length)
                {
                    return resultList;
                }

                foreach (string word in words)
                {
                    if (!dic.ContainsKey(word))
                    {
                        dic.Add(word, 1);
                    }
                    else
                    {
                        dic[word]++;
                    }
                }

                Dictionary<string, int> CloneDic = new Dictionary<string, int>(dic);


                int tempIndex = 0;
                bool changed = false;


                for (int i = 0; i <= s.Length - len * words.Length; i++)
                {
                    changed = false;
                    tempIndex = i;

                    for (int j = 0; j < words.Length; j++)
                    {
                        string sub = s.Substring(i, len);
                        i += len;

                        if (!CloneDic.ContainsKey(sub))
                        {
                            break;
                        }
                        else
                        {
                            if (CloneDic[sub] <= 0)
                            {
                                break;
                            }
                            else
                            {
                                changed = true;
                                CloneDic[sub]--;

                                if (j == words.Length - 1)
                                {
                                    resultList.Add(tempIndex);
                                    break;
                                }
                            }
                        }
                    }

                    // GoBack to tempIndex
                    i = tempIndex;

                    if (changed)
                    {
                        CloneDic = new Dictionary<string, int>(dic);
                    }
                }
            }

            return resultList;
        }

